import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { logout } from '../../store/actions/authActions';


class TopHeader extends Component {
    state = {
        isAuth: false,
    }
    static getDerivedStateFromProps(nextProps, prevState) {
        if (JSON.stringify(nextProps.auth.isAuth) === JSON.stringify(prevState.isAuth)) return null
        return {
            isAuth: nextProps.auth.isAuth,
            user: nextProps.auth.user
        }
    }
    render() {
        let { isAuth } = this.state
        return (
            <div className='top-header'>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6">
                            <ul className="contact-list d-flex">
                                <li><a href='#blank'><i className="fa fa-phone" /> +880555445454</a></li>
                                <li>|</li>
                                <li><a href='mailto:info@someone.com'><i className="fa fa-envelope-o" /> info@someone.com</a></li>
                            </ul>
                        </div>
                        <div className="col-lg-6">
                            <div className="float-right">
                                <ul className="contact-list d-flex">
                                    <li><a href='#blank'><i className="fa fa-facebook-official" /></a></li>
                                    <li>|</li>
                                    <li><a href='#blank'><i className="fa fa-youtube-play" /></a></li>
                                    <li>|</li>
                                    <li><a href='#blank'><i className="fa fa-twitter" /></a></li>
                                    <li>|</li>
                                    {!isAuth ? <li><Link to='/register'>REGISTER</Link></li> : <li onClick={() => this.props.logout(this.props.history)}>LOGOUT</li>}
                                    {!isAuth && <li>|</li>}
                                    {!isAuth && <li><Link to='/login'>LOGIN</Link></li>}
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    auth: state.auth,
})
export default connect(mapStateToProps, { logout })(TopHeader)
