import React, { Component, Fragment } from 'react'
import { Link, NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import logoImage from '../assets/images/logo.png';
import Navbar from './Navbar';
import ProfileImg from "../assets/images/client/2.png"
import ProfileMenu from './ProfileMenu';
import { getMarquee } from '../../store/actions/marqueeActions';
import TopHeader from './TopHeader';


class Header extends Component {
    state = {
        isAuth: false,
        isHeaderOpen: false,
        isProfileOpen: false,
        marquee: [],
    }
    static getDerivedStateFromProps(nextProps, prevState) {
        if (nextProps.auth.isAuth === prevState.isAuth) return null
        return {
            isAuth: nextProps.auth.isAuth,
        }
    }
    async componentDidMount() {
        this.setState({ marquee: await this.props.getMarquee() })
    }
    render() {
        let { isAuth, isHeaderOpen, isProfileOpen } = this.state
        let { history, auth } = this.props

        let imgSrc = ProfileImg
        let siteLogo = logoImage

        if (Object.keys(auth.user).length !== 0 && auth.user.file) {
            imgSrc = auth.user.file
            if (auth.user.logo) {
                siteLogo = auth.user.logo
            }
        }

        let { user } = auth
        let { marquee } = this.state
        return (
            <Fragment>
                <nav className="header-area fixed-top">
                    {marquee.length > 0 &&
                        <marquee scrollamount='5' className='d-block w-100 text-white font-weight-bold bg-primary'>
                            {marquee.map(item => <Link to={`/marquee/detail/${item._id}`} key={item._id} className='mx-5 text-white'>
                                {item.title}</Link>)}
                        </marquee>
                    }
                    <TopHeader history={history} />
                    <div className="container">
                        <div className='navbar navbar-expand-lg navbar-light'>
                            <Link to='/' className="navbar-brand">
                                <img src={siteLogo} className="bglogo float-left" alt="site logo" />
                                <div>
                                    {user.companyName}<br />
                                    {user.logoText ? user.logoText : 'স্বপ্ন ও স্বাধীনতা এখানে'}
                                </div>
                            </Link>
                            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navtogglerItemItem"
                                aria-controls="navtogglerItemItem" aria-expanded="false" aria-label="Toggle navigation">
                                <span className="navbar-toggler-icon"></span>
                            </button>
                            <div className="collapse navbar-collapse" id="navtogglerItemItem">
                                <ul className="navbar-nav ml-auto mt-2 mt-lg-0" id="main-menu">
                                    <li className="nav-item">
                                        <NavLink activeClassName="active" className="nav-link" to='/'>Home</NavLink>
                                    </li>

                                    {isAuth && <li className="nav-item" onClick={() => this.setState({ isProfileOpen: !isProfileOpen })}>
                                        <NavLink activeClassName="active" to='#blank'><img src={imgSrc} alt="pic" className="profile-image" /> {user.name} <i className="fa fa-caret-down" /></NavLink>

                                        {isProfileOpen && <ProfileMenu menuHandler={() => this.setState({ isProfileOpen: false })} history={history} />}

                                    </li>}


                                    {isAuth ?
                                        <li
                                            className="nav-item"
                                            onClick={() => this.setState({ isHeaderOpen: !isHeaderOpen })}
                                        >
                                            <i className="fa fa-bars menu-brand-icon" />
                                        </li> :
                                        <li className="nav-item">
                                            <NavLink activeClassName="active" className="nav-link" to='/login'>Sign Up/ Sign In</NavLink>
                                        </li>
                                    }

                                </ul>
                            </div>
                        </div>
                    </div>
                </nav>
                {isAuth && isHeaderOpen &&
                    <Navbar
                        menuHandler={() => this.setState({ isHeaderOpen: false })}
                        history={history}
                    />
                }

            </Fragment>
        )
    }
}
const mapStateToProps = state => ({
    auth: state.auth,
})
export default connect(mapStateToProps, { getMarquee })(Header)
