import { SET_MESSAGE, SET_ACTION, SET_AIRPORTS, SET_AIRLINES, SET_BASIC_INFO } from '../actions/types'

const init = {
    message: null,
    type: null,
    time: null,
    actionStatus: 1, /* 1=Before submit, 2=Submit is loading, 3=Fail, 4=Success */
    airports: [],
    airlines: [],
    countries: [],
    basicInfo: {},
    usefulLink: [],
    footerLogo: [],
    sliders: [],
    packages: [],
    services: [],
}

const commonReducer = (state = init, action) => {
    switch (action.type) {
        case SET_MESSAGE:
            {
                return {
                    ...state,
                    message: action.payload.message,
                    type: action.payload.type || 'success',
                    time: new Date().toLocaleString(),
                    actionStatus: (action.payload.type === 'success' && 4) || (action.payload.type === 'error' && 3),
                }
            }
        case SET_ACTION:
            {
                return {
                    ...state,
                    actionStatus: action.payload
                }
            }
        case SET_AIRPORTS:
            {
                return {
                    ...state,
                    airports: action.payload
                }
            }
        case SET_AIRLINES:
            {
                return {
                    ...state,
                    airlines: action.payload
                }
            }
        case SET_BASIC_INFO:
            {
                return {
                    ...state,
                    airlines: action.payload.airlines,
                    countries: action.payload.countries,
                    basicInfo: action.payload.basicInfo,
                    usefulLink: action.payload.usefulLink,
                    footerLogo: action.payload.footerLogo,
                    sliders: action.payload.sliders,
                    packages: action.payload.packages,
                    services: action.payload.services,
                }
            }
        default:
            return state
    }
}
export default commonReducer
