import jwtDecode from 'jwt-decode';
import * as Types from '../actions/types';

const init = {
    isAuth: false,
    user: {},
    loginAction: 0,
    registerStatus: 0, /* 0=Before Submit, 1=Loading, 2=Success, 3=Failed */
    loginStatus: 0,
    forgotStatus: 0,
    restPssStatus: 0,
}

const authReducer = (state = init, action) => {
    switch (action.type) {
        case Types.SET_USER: {
            return {
                ...state,
                user: action.payload && Object.keys(action.payload).length ? jwtDecode(action.payload) : {},
                isAuth: action.payload && Object.keys(action.payload).length !== 0,
            }
        }
        case Types.REGISTER_STATUS: {
            return {
                ...state,
                registerStatus: action.payload,
            }
        }
        case Types.LOGIN_STATUS: {
            return {
                ...state,
                loginStatus: action.payload,
            }
        }
        case Types.FORGOT_STATUS: {
            return {
                ...state,
                forgotStatus: action.payload,
            }
        }
        case Types.RESET_PASSWORD: {
            return {
                ...state,
                resetPassStatus: action.payload,
            }
        }
        default: return state
    }
}
export default authReducer
