import React, { Component, Fragment } from 'react'
import { Link, NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import TopHeader from './TopHeader';


class Header extends Component {
    state = {
        isAuth: false,
        user: {}
    }
    static getDerivedStateFromProps(nextProps, prevState) {
        if (JSON.stringify(nextProps.auth.isAuth) === JSON.stringify(prevState.isAuth)) return null
        return {
            isAuth: nextProps.auth.isAuth,
            user: nextProps.auth.user
        }
    }
    render() {
        let { isAuth, user } = this.state

        let siteLogo = ''
        return (
            <Fragment>
                <nav className="header-area fixed-top">
                    <TopHeader history={this.props.history} />
                    <div className="container">
                        <div className='navbar navbar-expand-lg navbar-light'>
                            <Link to='/' className="navbar-brand">
                                <img src={siteLogo} className="bglogo float-left" alt="site logo" />
                            </Link>
                            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navtogglerItemItem"
                                aria-controls="navtogglerItemItem" aria-expanded="false" aria-label="Toggle navigation">
                                <span className="navbar-toggler-icon"></span>
                            </button>
                            <div className="collapse navbar-collapse" id="navtogglerItemItem">
                                <ul className="navbar-nav ml-auto mt-2 mt-lg-0" id="main-menu">
                                    <li className="nav-item">
                                        <NavLink activeClassName="active" className="nav-link" to='/'>Home</NavLink>
                                    </li>

                                    {isAuth && <li className="nav-item">
                                        <NavLink activeClassName="active" to='#blank'><img src={imgSrc} alt="pic" className="profile-image" /> {user.name}</NavLink>
                                    </li>}
                                </ul>
                            </div>
                        </div>
                    </div>
                </nav>
            </Fragment>
        )
    }
}
const mapStateToProps = state => ({
    auth: state.auth,
})
export default connect(mapStateToProps)(Header)
